mapper.insert:
INSERT INTO oauth_client_details(
    client_id, resource_ids, client_secret, scope, authorized_grant_types,
    web_server_redirect_uri, authorities, access_token_validity, refresh_token_validity,
    additional_information, autoapprove
)
VALUES (
    #{clientId}, #{resourceIds}, #{clientSecret}, #{scope}, #{authorizedGrantTypes},
    #{webServerRedirectUri}, #{authorities}, #{accessTokenValidity}, #{refreshTokenValidity},
    #{additionalInformation}, #{autoapprove}
)

mapper.update:
UPDATE oauth_client_details SET resource_ids = #{client.resourceIds},
client_secret = #{client.clientSecret},
scope = #{client.scope}, authorized_grant_types = #{client.authorizedGrantTypes}, web_server_redirect_uri = #{client.webServerRedirectUri}, authorities = #{client.authorities}, access_token_validity = #{client.accessTokenValidity}, refresh_token_validity = #{client.refreshTokenValidity}, additional_information = #{client.additionalInformation}, autoapprove = #{client.autoapprove} WHERE client_id = #{clientId}
