mapper.select:
SELECT

FROM sys_resource


        AND name LIKE CONCAT('%', #{name}, '%')


        AND original_name LIKE CONCAT('%', #{originalName}, '%')


        AND file_type = #{fileType}


        AND create_time <= #{createTime}


ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM sys_resource


        AND name LIKE CONCAT('%', #{name}, '%')


        AND original_name LIKE CONCAT('%', #{originalName}, '%')


        AND file_type = #{fileType}


        AND create_time <= #{createTime}

mapper.insert:
INSERT INTO sys_resource(
    name, original_name, file_path, file_type, file_size, mime_type, url, description, tenant_id
)
VALUES (
    #{name}, #{originalName}, #{filePath}, #{fileType}, #{fileSize}, #{mimeType}, #{url}, #{description}, #{tenantId}
)

mapper.update:
UPDATE sys_resource
SET name = #{resource.name},
    description = #{resource.description}
WHERE id = #{resourceId}
